import React, { useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const Sidebar = () => {
  const { user } = useContext(AuthContext);
  const location = useLocation();

  if (!user) return null;

  let links = [];

  if (user.role === 'admin') {
    links = [
      { path: '/admin/users', label: 'Manage Users' },
      { path: '/admin/courses', label: 'Manage Courses' },
      { path: '/admin/bookings', label: 'Manage Bookings' },
      { path: '/admin/notifications', label: 'Send Notifications' },
      { path: '/admin/reports', label: 'Reports' },
      { path: '/courses', label: 'Browse Courses', icon: 'üìö' }, // Added Browse Courses
    ];
  } else if (user.role === 'student') {
    links = [
      { path: '/student/dashboard', label: 'Dashboard' },
      { path: '/student/profile', label: 'Profile' },
      { path: '/payment', label: 'Make Payment', icon: 'üí≥' },
      { path: '/courses', label: 'Browse Courses', icon: 'üìö' }, // Added Browse Courses
      { path: '/messages', label: 'Messages', icon: '‚úâÔ∏è' },
      { path: '/chat/:recipientId', label: 'Chat', icon: 'üí¨' },
    ];
  } else if (user.role === 'faculty') {
    links = [
      { path: '/faculty/dashboard', label: 'Dashboard' },
      { path: '/faculty/profile', label: 'Profile' },
      { path: '/faculty/payment', label: 'Payment' },
      { path: '/courses', label: 'Browse Courses', icon: 'üìö' }, // Added Browse Courses
    ];
  }

  return (
    <div className="sidebar bg-light p-3" style={{ width: '200px', height: '100vh' }}>
      <ul className="list-unstyled">
        {links.map((link) => (
          <li key={link.path} className={`mb-2 ${location.pathname === link.path ? 'fw-bold' : ''}`}>
            <Link to={link.path} className="text-decoration-none text-dark">
              {link.icon && <span className="me-2">{link.icon}</span>}
              {link.label}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Sidebar;
