// frontend/src/components/ChatBox.js
import React, { useEffect, useState, useRef } from 'react';
import {
  setupSocket,
  subscribeToMessages,
  subscribeToMessageSent,
  sendMessageWS,
  disconnectSocket,
} from '../services/socket';
import api from '../services/api';

const ChatBox = ({ recipient, currentUserId }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const bottomRef = useRef(null);

  // Scroll to the bottom
  const scrollToBottom = () => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    // guard: only run when we know both participants
    if (!recipient?._id || !currentUserId) return;
    let isMounted = true;

    const fetchMessages = async () => {
      try {
        const res = await api.get('/messages');
        const conv = res.data.filter(msg => {
          return (
            (msg.sender === recipient._id && msg.recipient === currentUserId) ||
            (msg.sender === currentUserId && msg.recipient === recipient._id)
          );
        });
        if (isMounted) {
          setMessages(conv);
          scrollToBottom();
        }
      } catch (err) {
        console.error('Error loading messages:', err);
      }
    };

    fetchMessages();
    setupSocket();

    // shared handler for incoming/ack'd messages
    const handleIncoming = msg => {
      if (!msg) return;
      const { sender, recipient: msgRecipient } = msg;
      if (
        (sender === recipient._id && msgRecipient === currentUserId) ||
        (sender === currentUserId && msgRecipient === recipient._id)
      ) {
        setMessages(prev => [...prev, msg]);
        scrollToBottom();
      }
    };

    subscribeToMessages(handleIncoming);
    subscribeToMessageSent(handleIncoming);

    return () => {
      isMounted = false;
      disconnectSocket();
    };
  }, [recipient, currentUserId]);

  const handleSend = e => {
    e.preventDefault();
    const text = input.trim();
    if (!text) return;
    if (!recipient?._id || !currentUserId) {
      console.error('ChatBox: missing recipient or current user ID');
      return;
    }
    sendMessageWS(recipient._id, text);
    setInput('');
  };

  return (
    <div className="chatbox" style={{ border: '1px solid #ccc', padding: 12, borderRadius: 8 }}>
      <div style={{ height: 300, overflowY: 'auto', marginBottom: 8 }}>
        {messages.map(msg => (
          <div
            key={msg._id}
            style={{
              textAlign: msg.sender === recipient._id ? 'left' : 'right',
              marginBottom: 4,
            }}
          >
            <span
              style={{
                background: msg.sender === recipient._id ? '#eee' : '#b3d4fc',
                padding: '6px 10px',
                borderRadius: 12,
                display: 'inline-block',
                maxWidth: '80%',
                wordWrap: 'break-word',
              }}
            >
              {msg.body}
            </span>
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      <form onSubmit={handleSend} style={{ display: 'flex' }}>
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          placeholder="Type a message..."
          style={{
            flex: 1,
            padding: '8px',
            border: '1px solid #ccc',
            borderRadius: '4px',
            marginRight: '8px',
            fontSize: '14px',
          }}
        />
        <button
          type="submit"
          style={{ padding: '8px 12px', border: '1px solid #ccc', borderRadius: '4px', cursor: 'pointer' }}
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatBox;
