// client/src/components/PaymentPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentPage = () => {
    const [products, setProducts] = useState([]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [total, setTotal] = useState(0);

    useEffect(() => {
        // Fetch products from backend
        axios.get('/api/products')
            .then(response => {
                setProducts(response.data);
            })
            .catch(error => {
                console.error('Error fetching products:', error);
            });
    }, []);

    useEffect(() => {
        // Calculate total whenever selectedItems changes
        const calculatedTotal = selectedItems.reduce((sum, item) => {
            return sum + (item.price * item.quantity);
        }, 0);
        setTotal(calculatedTotal);
    }, [selectedItems]);

    const handleQuantityChange = (productId, quantity) => {
        const quantityNum = parseInt(quantity) || 0;

        if (quantityNum < 0) return;

        setSelectedItems(prevItems => {
            // Remove if quantity is 0
            if (quantityNum === 0) {
                return prevItems.filter(item => item.id !== productId);
            }

            // Update or add item
            const existingItemIndex = prevItems.findIndex(item => item.id === productId);
            if (existingItemIndex >= 0) {
                const updatedItems = [...prevItems];
                updatedItems[existingItemIndex] = {
                    ...updatedItems[existingItemIndex],
                    quantity: quantityNum
                };
                return updatedItems;
            } else {
                const product = products.find(p => p.id === productId);
                return [...prevItems, { ...product, quantity: quantityNum }];
            }
        });
    };

    const handlePayment = () => {
        // Here you would typically integrate with a payment gateway
        alert(`Proceeding to payment for $${total.toFixed(2)}`);
        // In a real app, you would redirect to a payment processor
    };

    return (
        <div className="payment-page">
            <h2>Select Items</h2>
            <div className="product-list">
                {products.map(product => (
                    <div key={product.id} className="product-item">
                        <h3>{product.name}</h3>
                        <p>Price: ${product.price.toFixed(2)}</p>
                        <input
                            type="number"
                            min="0"
                            value={selectedItems.find(item => item.id === product.id)?.quantity || 0}
                            onChange={(e) => handleQuantityChange(product.id, e.target.value)}
                        />
                    </div>
                ))}
            </div>
            <div className="total-section">
                <h3>Total: ${total.toFixed(2)}</h3>
                <button
                    onClick={handlePayment}
                    disabled={total <= 0}
                >
                    Proceed to Payment
                </button>
            </div>
        </div>
    );
};

export default PaymentPage;
