import React, { useEffect, useState } from 'react';
import api from '../services/api';
import './styling.css';

const ManageBookings = () => {
  const [bookings, setBookings] = useState([]);

  // Fetch bookings from the backend
  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const res = await api.get('/admin/bookings');
        setBookings(res.data);
      } catch (err) {
        console.error('Failed to fetch bookings', err);
      }
    };
    fetchBookings();
  }, []);

  // Handle booking cancellation
  const cancelBooking = async (id) => {
    try {
      await api.delete(`/admin/bookings/${id}`); // Cancel booking via backend
      setBookings(bookings.filter((booking) => booking._id !== id)); // Update local state
    } catch (err) {
      console.error('Failed to cancel booking', err);
    }
  };

  // Handle conflict resolution
  const resolveConflict = async (id) => {
    try {
      await api.put(`/admin/bookings/${id}/resolve`); // Resolve conflict via backend
      setBookings(bookings.map((booking) => 
        booking._id === id ? { ...booking, conflictResolved: true } : booking
      )); // Update conflict status in local state
    } catch (err) {
      console.error('Failed to resolve conflict', err);
    }
  };

  return (
    <div className="manage-bookings-container">
      <h2 className="heading">Manage Field Bookings</h2>
      <table className="booking-table">
        <thead>
          <tr>
            <th>User</th>
            <th>Facility</th>
            <th>Booking Time</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking._id}>
              <td>{booking.user ? `${booking.user.name} (${booking.user.email})` : 'Unknown'}</td>
              <td>{booking.facility}</td>
              <td>{`${new Date(booking.startTime).toLocaleString()} - ${new Date(booking.endTime).toLocaleString()}`}</td>
              <td>
                {booking.status === 'cancelled' ? (
                  <span className="cancelled">Cancelled</span>
                ) : booking.status === 'approved' ? (
                  <span className="approved">Approved</span>
                ) : (
                  <span>{booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}</span>
                )}
              </td>
              <td>
                {booking.status !== 'cancelled' && (
                  <button className="cancel-btn" onClick={() => cancelBooking(booking._id)}>Cancel</button>
                )}
                {!booking.conflictResolved && (
                  <button className="resolve-btn" onClick={() => resolveConflict(booking._id)}>Resolve Conflict</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageBookings;
