// client/src/components/PaymentPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PaymentPage.css';

const PaymentPage = () => {
    const [products, setProducts] = useState([
        { id: 1, name: 'CSE110', price: 22000.00 },
        { id: 2, name: 'CSE111', price: 22000.00 },
        { id: 3, name: 'CSE112', price: 22000.00 }
    ]);
    const [selectedItems, setSelectedItems] = useState([]);
    const [total, setTotal] = useState(0);

    useEffect(() => {
        const calculatedTotal = selectedItems.reduce((sum, item) => {
            return sum + item.price;
        }, 0);
        setTotal(calculatedTotal);
    }, [selectedItems]);

    const handleCheckboxChange = (productId, isChecked) => {
        setSelectedItems(prevItems => {
            if (isChecked) {
                const product = products.find(p => p.id === productId);
                return [...prevItems, product];
            } else {
                return prevItems.filter(item => item.id !== productId);
            }
        });
    };

    const generateReceipt = () => {
        // Create receipt content
        const receiptContent = `
            Receipt
            ----------------------------
            ${selectedItems.map(item => `${item.name}: $${item.price.toFixed(2)}`).join('\n')}
            ----------------------------
            Total: $${total.toFixed(2)}
            Date: ${new Date().toLocaleDateString()}
            Thank you for your payment!
        `;

        // Create a Blob with the receipt content
        const blob = new Blob([receiptContent], { type: 'text/plain' });
        const url = URL.createObjectURL(blob);

        // Create a download link
        const link = document.createElement('a');
        link.href = url;
        link.download = `receipt_${new Date().getTime()}.txt`;
        document.body.appendChild(link);
        link.click();

        // Clean up
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
    };

    return (
        <div className="payment-page">
            <h2>Select Items to Pay</h2>
            <div className="product-list">
                {products.map(product => (
                    <div key={product.id} className="product-item">
                        <label className="checkbox-label">
                            <input
                                type="checkbox"
                                checked={selectedItems.some(item => item.id === product.id)}
                                onChange={(e) => handleCheckboxChange(product.id, e.target.checked)}
                            />
                            <span className="product-info">
                                <span className="product-name">{product.name}</span>
                                <span className="product-price">${product.price.toFixed(2)}</span>
                            </span>
                        </label>
                    </div>
                ))}
            </div>
            <div className="total-section">
                <h3>Total: {total.toFixed(2)}</h3>
                <button
                    onClick={generateReceipt}
                    disabled={total <= 0}
                    className="receipt-button"
                >
                    Download Your Receipt
                </button>
            </div>
        </div>
    );
};

export default PaymentPage;
