// src/components/ManageUsers.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { Button, Table } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await api.get('/admin/users');
        setUsers(res.data);
      } catch (err) {
        alert('Failed to fetch users. Check permissions.');
      }
    };
    fetchUsers();
  }, []);

  const handleAddUser = () => {
    navigate('/admin/users/add'); // Redirect to a page where you can add a new user
  };

  const handleEditUser = (userId) => {
    navigate(`/admin/users/edit/${userId}`); // Redirect to a page for editing user
  };

  return (
    <div>
      <h3>Manage Users</h3>
      <Button onClick={handleAddUser} className="mb-3">Add User</Button>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <Button onClick={() => handleEditUser(user._id)} variant="warning">Edit</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default ManageUsers;
