// src/components/ManageCourses.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useParams, useNavigate } from 'react-router-dom';

const ManageCourses = () => {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({ name: '', description: '' });
  const [editingCourseId, setEditingCourseId] = useState(null);
  const [editData, setEditData] = useState({ name: '', description: '' });
  const navigate = useNavigate();
  const { courseId } = useParams(); // For Edit functionality, get the courseId from the URL

  // Fetch courses
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const res = await api.get('/admin/courses');
        setCourses(res.data);
      } catch (err) {
        console.error('Failed to fetch courses', err);
      }
    };
    fetchCourses();
  }, []);

  // Add new course
  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/admin/courses', newCourse);
      setCourses([...courses, res.data]);
      setNewCourse({ name: '', description: '' });
    } catch (err) {
      console.error('Failed to add course:', err);
    }
  };

  // Start editing
  const handleEditClick = (course) => {
    setEditingCourseId(course._id);
    setEditData({ name: course.name, description: course.description });
  };

  // Update course
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/admin/courses/${editingCourseId}`, editData);
      setCourses(
        courses.map((course) =>
          course._id === editingCourseId ? { ...course, ...editData } : course
        )
      );
      setEditingCourseId(null);
      setEditData({ name: '', description: '' });
    } catch (err) {
      console.error('Failed to update course:', err);
    }
  };

  // Delete course
  const deleteCourse = async (id) => {
    try {
      await api.delete(`/admin/courses/${id}`);
      setCourses(courses.filter((course) => course._id !== id));
    } catch (err) {
      console.error('Failed to delete course:', err);
    }
  };

  return (
    <div>
      <h2>Manage Courses</h2>

      {/* Add New Course */}
      <form onSubmit={handleAdd} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Course Name"
          value={newCourse.name}
          onChange={(e) => setNewCourse({ ...newCourse, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={newCourse.description}
          onChange={(e) =>
            setNewCourse({ ...newCourse, description: e.target.value })
          }
          required
        />
        <button type="submit">Add Course</button>
      </form>

      {/* Course List */}
      <table border="1" cellPadding="8" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>Course Name</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) =>
            editingCourseId === course._id ? (
              <tr key={course._id}>
                <td>
                  <input
                    value={editData.name}
                    onChange={(e) =>
                      setEditData({ ...editData, name: e.target.value })
                    }
                  />
                </td>
                <td>
                  <input
                    value={editData.description}
                    onChange={(e) =>
                      setEditData({ ...editData, description: e.target.value })
                    }
                  />
                </td>
                <td>
                  <button onClick={handleUpdate}>Save</button>
                  <button onClick={() => setEditingCourseId(null)}>Cancel</button>
                </td>
              </tr>
            ) : (
              <tr key={course._id}>
                <td>{course.name}</td>
                <td>{course.description}</td>
                <td>
                  <button onClick={() => handleEditClick(course)}>Edit</button>
                  <button onClick={() => deleteCourse(course._id)}>Delete</button>
                </td>
              </tr>
            )
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ManageCourses;
