// src/components/ManageUsers.js
import React, { useEffect, useState } from 'react';
import api from '../services/api';
import { useNavigate, useParams } from 'react-router-dom';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null); // State for editing user
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    role: 'staff', // Default role
  });

  const navigate = useNavigate();
  const { userId } = useParams(); // For Edit functionality, get the userId from the URL

  // Fetch all users
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await api.get('/admin/users');
        setUsers(res.data);
      } catch (err) {
        console.error('Failed to fetch users', err);
      }
    };
    fetchUsers();
  }, []);

  // Handle Add User
  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/admin/users', newUser);
      setUsers([...users, res.data]); // Add the new user to the users list
      setNewUser({ name: '', email: '', role: 'staff' }); // Reset form
    } catch (err) {
      console.error('Failed to add user', err);
    }
  };

  // Handle Edit User
  useEffect(() => {
    if (userId) {
      const userToEdit = users.find((user) => user._id === userId);
      if (userToEdit) {
        setEditingUser(userToEdit);
      }
    }
  }, [userId, users]);

  const handleEditUser = async (e) => {
    e.preventDefault();
    try {
      const res = await api.put(`/admin/users/${editingUser._id}`, editingUser);
      const updatedUsers = users.map((user) =>
        user._id === editingUser._id ? res.data : user
      );
      setUsers(updatedUsers);
      setEditingUser(null); // Reset editing state
      navigate('/admin/users'); // Navigate back to the user list
    } catch (err) {
      console.error('Failed to update user', err);
    }
  };

  // Handle Delete User
  const deleteUser = async (id) => {
    try {
      await api.delete(`/admin/users/${id}`);
      setUsers(users.filter((user) => user._id !== id));
    } catch (err) {
      console.error('Failed to delete user', err);
    }
  };

  // Handle input change for new user
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewUser({
      ...newUser,
      [name]: value,
    });
  };

  // Handle input change for editing user
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingUser({
      ...editingUser,
      [name]: value,
    });
  };

  return (
    <div>
      <h2>Manage Users</h2>

      {/* Add User Form */}
      {!editingUser && (
        <div>
          <h3>Add User</h3>
          <form onSubmit={handleAddUser}>
            <div>
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={newUser.name}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={newUser.email}
                onChange={handleInputChange}
                required
              />
            </div>
            <div>
              <label>Role</label>
              <select
                name="role"
                value={newUser.role}
                onChange={handleInputChange}
                required
              >
                <option value="staff">Staff</option>
                <option value="admin">Admin</option>
                <option value="faculty">Faculty</option>
              </select>
            </div>
            <button type="submit">Add User</button>
          </form>
        </div>
      )}

      {/* Edit User Form */}
      {editingUser && (
        <div>
          <h3>Edit User</h3>
          <form onSubmit={handleEditUser}>
            <div>
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={editingUser.name}
                onChange={handleEditChange}
                required
              />
            </div>
            <div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editingUser.email}
                onChange={handleEditChange}
                required
              />
            </div>
            <div>
              <label>Role</label>
              <select
                name="role"
                value={editingUser.role}
                onChange={handleEditChange}
                required
              >
                <option value="staff">Staff</option>
                <option value="admin">Admin</option>
                <option value="faculty">Faculty</option>
              </select>
            </div>
            <button type="submit">Save Changes</button>
          </form>
        </div>
      )}

      {/* Display Users */}
      <div>
        <h3>Users List</h3>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
                <td>
                  <button onClick={() => deleteUser(user._id)}>Delete</button>
                  <button onClick={() => navigate(`/admin/users/edit/${user._id}`)}>
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ManageUsers;
