import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseSearch = () => {
    const [titleSearch, setTitleSearch] = useState('');
    const [codeSearch, setCodeSearch] = useState('');
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(false);

    const fetchCourses = async () => {
        if (titleSearch.trim() === '' && codeSearch.trim() === '') {
            setCourses([]); // Clear courses if no search term
            return;
        }

        setLoading(true);
        try {
            const res = await axios.get('/api/courses', {
                params: {
                    title: titleSearch,
                    code: codeSearch
                },
                withCredentials: true,
            });
            setCourses(res.data);
        } catch (err) {
            console.error('Error fetching courses:', err);
        }
        setLoading(false);
    };

    useEffect(() => {
        // Only fetch courses when search input changes, and debounce the requests
        const delayDebounceFn = setTimeout(() => {
            fetchCourses();
        }, 500); // 500ms delay after the user stops typing

        return () => clearTimeout(delayDebounceFn); // Cleanup the previous timeout if input changes
    }, [titleSearch, codeSearch]); // Fetch courses when either search term changes

    return (
        <div style={{ maxWidth: '600px', margin: 'auto', padding: '2rem' }}>
            <h2>Search Courses</h2>
            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    placeholder="Search by course title"
                    value={titleSearch}
                    onChange={(e) => setTitleSearch(e.target.value)}
                    style={{ width: '48%', padding: '10px', marginRight: '4%' }}
                />
                <input
                    type="text"
                    placeholder="Search by course code"
                    value={codeSearch}
                    onChange={(e) => setCodeSearch(e.target.value)}
                    style={{ width: '48%', padding: '10px' }}
                />
            </div>

            {loading ? (
                <p>Loading...</p>
            ) : courses.length === 0 && (titleSearch.trim() !== '' || codeSearch.trim() !== '') ? (
                <p>No courses found.</p>
            ) : (
                <ul>
                    {courses.map((course) => (
                        <li key={course._id} style={{ marginBottom: '10px' }}>
                            <strong>{course.title}</strong> â€” {course.description ? course.description : 'No description available'}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default CourseSearch;
