import React, { useEffect, useState } from 'react';
import api from '../services/api';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true); // New state for loading
  const [error, setError] = useState(''); // New state for error

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await api.get('/admin/users');
        setUsers(res.data);
        setLoading(false); // Set loading to false once data is fetched
      } catch (err) {
        console.error('Error fetching users:', err); // Log the error details
        setError('Failed to fetch users. Check permissions.');
        setLoading(false); // Set loading to false even on error
      }
    };

    fetchUsers();
  }, []);

  if (loading) {
    return <div>Loading users...</div>; // Show loading state while fetching
  }

  return (
    <div>
      {error && <div className="alert alert-danger">{error}</div>} {/* Show error if any */}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageUsers;
